import React from 'react';

export default function Filters({filters, setFilters, locations, industries}){
  const onChange = (e)=> setFilters(f=> ({...f,[e.target.name]: e.target.value}));

  return (
    <div className="flex flex-col md:flex-row gap-4 md:items-center md:justify-between mb-6">
      <div className="flex gap-3 flex-1">
        <input
          name="q"
          value={filters.q}
          onChange={onChange}
          placeholder="Search company name..."
          className="border p-2 rounded w-full"
        />
        <select name="location" value={filters.location} onChange={onChange} className="border p-2 rounded">
          <option value="">All Locations</option>
          {locations.map(l=> <option key={l} value={l}>{l}</option>)}
        </select>
        <select name="industry" value={filters.industry} onChange={onChange} className="border p-2 rounded">
          <option value="">All Industries</option>
          {industries.map(i=> <option key={i} value={i}>{i}</option>)}
        </select>
      </div>

      <div className="flex gap-2">
        <select name="sort" value={filters.sort} onChange={onChange} className="border p-2 rounded">
          <option value="name_asc">Name A → Z</option>
          <option value="name_desc">Name Z → A</option>
        </select>
        <select name="pageSize" value={filters.pageSize} onChange={onChange} className="border p-2 rounded">
          <option value={6}>6 / page</option>
          <option value={9}>9 / page</option>
          <option value={12}>12 / page</option>
        </select>
      </div>
    </div>
  );
}
