import React from 'react';
import { motion } from 'framer-motion';

export default function CompanyCard({c}){
  return (
    <motion.div
      whileHover={{scale:1.03, boxShadow:'0 6px 18px rgba(0,0,0,0.1)'}}
      initial={{opacity:0, y:20}}
      animate={{opacity:1, y:0}}
      transition={{duration:0.4}}
      className="rounded-2xl bg-white p-5 border border-gray-100 shadow-sm hover:shadow-lg hover:border-blue-200 transition"
    >
      <h3 className="text-lg font-semibold text-gray-800 mb-1">{c.name}</h3>
      <p className="text-sm text-gray-500">{c.industry} â€¢ {c.location}</p>
      <div className="mt-2 text-sm text-gray-600">Employees: <span className="font-medium">{c.employees}</span></div>
    </motion.div>
  );
}
