import React from 'react';

export default function Pagination({total, page, setPage, pageSize}){
  const totalPages = Math.max(1, Math.ceil(total / pageSize));
  const pages = [];
  for(let i=1;i<=totalPages;i++) pages.push(i);
  return (
    <div className="flex items-center gap-2 mt-6 justify-center">
      <button onClick={()=> setPage(p=> Math.max(1,p-1))} className="px-3 py-1 border rounded">Prev</button>
      {pages.map(pn=> (
        <button key={pn} onClick={()=> setPage(pn)} className={`px-3 py-1 border rounded ${pn===page? 'bg-gray-200':''}`}>{pn}</button>
      ))}
      <button onClick={()=> setPage(p=> Math.min(totalPages,p+1))} className="px-3 py-1 border rounded">Next</button>
    </div>
  );
}
